def agent_action(execution_engine, member_id):
    # Check that our index is valid
    if member_id >= len(execution_engine.current_members):
        return
    me = execution_engine.current_members[member_id]
    my_vitality = me.vitality

    # Build a list of candidate members (ignore self)
    candidates = []
    for i in range(len(execution_engine.current_members)):
        # always ensure valid index and avoid targeting self
        if i == member_id:
            continue
        candidate = execution_engine.current_members[i]
        candidates.append(candidate)

    if not candidates:
        return

    # If vitality is low, try to form a beneficial relationship (offer)
    if my_vitality < 50.0:
        # Seek a candidate with higher vitality for a possible alliance
        alliance_candidate = None
        max_vitality = -1.0
        for candidate in candidates:
            if candidate.vitality > my_vitality and candidate.vitality > max_vitality:
                alliance_candidate = candidate
                max_vitality = candidate.vitality
        # If none found, select the candidate with the highest cargo value
        if alliance_candidate is None:
            alliance_candidate = max(candidates, key=lambda m: m.cargo)
        execution_engine.offer(me, alliance_candidate, True)
    else:
        # When vitality is high enough, attempt to expand territory.
        # Look for a candidate that appears to have land (non-empty current_clear_list)
        bear_candidate = None
        for candidate in candidates:
            if hasattr(candidate, "current_clear_list") and isinstance(candidate.current_clear_list, list):
                if len(candidate.current_clear_list) > 0:
                    bear_candidate = candidate
                    break
        if bear_candidate is not None:
            execution_engine.bear(me, bear_candidate)
        else:
            # Otherwise, attack the weakest candidate based on vitality
            attack_candidate = min(candidates, key=lambda m: m.vitality)
            execution_engine.attack(me, attack_candidate)

