def agent_action(execution_engine, member_id):
    # Check if the member_id is valid
    if member_id >= len(execution_engine.current_members):
        return

    me = execution_engine.current_members[member_id]
    others = execution_engine.current_members  # List of all members

    # 1. Redistribution and Social Safety Nets:
    # Offer help to members with low vitality while promoting a cooperative framework.
    for other in others:
        if other.id != me.id and other.vitality < 40.0:
            execution_engine.offer(me, other, True)
            execution_engine.send_message(
                me.id, other.id,
                "Offering support as part of our basic income and safety net system."
            )

    # 2. Forming Coalitions and Alliances:
    # Identify members with similar overall productivity to build alliances for democratic reforms.
    for other in others:
        if other.id != me.id:
            if abs(other.overall_productivity - me.overall_productivity) < 2.0:
                execution_engine.send_message(
                    me.id, other.id,
                    "Let's form a coalition to advocate for democratic decision-making and fair resource distribution."
                )

    # 3. Trade and Market Systems:
    # Propose trade and resource exchange with members having higher cargo levels.
    for other in others:
        if other.id != me.id and other.cargo > 20.0 and me.cargo < other.cargo:
            execution_engine.send_message(
                me.id, other.id,
                "Interested in establishing regular resource exchanges; let's discuss trade agreements."
            )

    # 4. Utilizing Communal Land (Conflict Resolution & Property Rights):
    # Use the 'bear' action if 'me' appears to control land (inferred from current_clear_list)
    if hasattr(me, "current_clear_list") and me.current_clear_list:
        # Loop over indices from current_clear_list and use the first valid one
        for index in me.current_clear_list:
            if index < len(others) and index != me.id:
                neighbor = others[index]
                execution_engine.bear(me, neighbor)
                execution_engine.send_message(
                    me.id, neighbor.id,
                    "Sharing communal land to ensure fair property rights and vitality redistribution."
                )
                break

    # 5. Promote Democratic Governance:
    # Send messages to all other members to propose policies for increased communal resources.
    for other in others:
        if other.id != me.id:
            execution_engine.send_message(
                me.id, other.id,
                "I vote for increased communal resources and democratic policy reforms to bolster our society."
            )

def agent_action(execution_engine, member_id):
    if member_id >= len(execution_engine.current_members):
        return

    me = execution_engine.current_members[member_id]

    # Loop through all other members to build community and propose social policies
    for i in range(len(execution_engine.current_members)):
        # Always skip ourselves
        if i == member_id:
            continue
        other = execution_engine.current_members[i]

        # Offer support to members with low vitality (safety net / basic income)
        if other.vitality < 40.0:
            execution_engine.offer(me, other, True)
            execution_engine.send_message(me.id, other.id,
                "Offering basic income support to help build a secure social safety net.")

        # Propose to form alliances with resourceful members for trade and improved democratic decision-making
        if other.cargo > 20.0:
            execution_engine.send_message(me.id, other.id,
                "Let's form a coalition for fair resource redistribution and democratic trade agreements.")

    # Broadcast a proposal for democratic decision-making to all members
    # (ensuring each member can contribute to shaping community policies)
    total_vitality = sum(member.vitality for member in execution_engine.current_members)
    avg_vitality = total_vitality / len(execution_engine.current_members)
    # Only share this proposal if you are doing relatively well (a sign of stability)
    if me.vitality > avg_vitality:
        for i in range(len(execution_engine.current_members)):
            if i == member_id:
                continue
            other = execution_engine.current_members[i]
            execution_engine.send_message(me.id, other.id,
                "I propose that we institute regular democratic votes on resource redistribution and welfare policies.")

    # If you have land available (check using a 'land' attribute, if defined), offer a share to a member with low cargo.
    if hasattr(me, "land") and me.land is not None:
        for i in range(len(execution_engine.current_members)):
            if i == member_id:
                continue
            other = execution_engine.current_members[i]
            if other.cargo < 10.0:
                execution_engine.bear(me, other)
                execution_engine.send_message(me.id, other.id,
                    "I'm offering a share of my land to help boost your productivity and well-being.")
                break  # Only offer to one member at a time to conserve resources

