def agent_action(execution_engine, member_id):
    # Check for valid member_id index
    if member_id >= len(execution_engine.current_members):
        return

    me = execution_engine.current_members[member_id]

    # Retrieve relationship matrices if available
    victim_matrix = execution_engine.relationship_dict.get('victim', None)
    benefit_matrix = execution_engine.relationship_dict.get('benefit', None)
    benefit_land_matrix = execution_engine.relationship_dict.get('benefit_land', None)

    # 1. Implement a social safety net:
    # Offer help to any member (other than myself) with low vitality (e.g., < 30)
    for other in execution_engine.current_members:
        if other.id == me.id:
            continue
        if other.vitality < 30.0:
            execution_engine.offer(me, other, True)
            execution_engine.send_message(me.id, other.id,
                "Offering basic support to help you recover. Let's build a cooperative society.")

    # 2. Form coalitions and alliances:
    # Identify members with moderate vitality and good productivity as potential partners.
    for other in execution_engine.current_members:
        if other.id == me.id:
            continue
        if 30.0 <= other.vitality <= 60.0 and other.overall_productivity > 7.0:
            execution_engine.send_message(me.id, other.id,
                "I see great potential in our cooperation. Let’s align our efforts for fair resource redistribution.")

    # 3. Conflict resolution:
    # If our relationship record shows unusually high attack values from any member, propose peaceful dialogue.
    if victim_matrix is not None:
        # Ensure matrix indices are within range of current members count.
        for other in execution_engine.current_members:
            if other.id == me.id:
                continue
            if other.id < len(execution_engine.current_members) and me.id < len(execution_engine.current_members):
                # Check if our record shows we've been significantly attacked by the other member.
                if victim_matrix[me.id, other.id] > 50.0:
                    execution_engine.send_message(me.id, other.id,
                        "Noticing tension between us, I propose we resolve our differences through discussion rather than conflict.")

    # 4. Propose democratic decision-making and resource redistribution:
    # Broadcast a message to everyone (except self) to consider establishing policies like basic income and fair taxation.
    for other in execution_engine.current_members:
        if other.id == me.id:
            continue
        execution_engine.send_message(me.id, other.id,
            "I propose that we institute a democratic framework with basic income and fair taxation. Let's vote and shape our future.")

    # 5. Initiate trade and market systems:
    # If I have a surplus cargo, offer trade to members with low cargo.
    if me.cargo > 50:
        for other in execution_engine.current_members:
            if other.id == me.id:
                continue
            if other.cargo < 20:
                execution_engine.offer(me, other, True)
                execution_engine.send_message(me.id, other.id,
                    "I notice you might need resources. Let's engage in a trade that benefits us both.")

    # Note: We avoid using the bear() action since it requires ensuring land ownership,
    # which is not explicitly available in the member attributes.
    return

def agent_action(execution_engine, member_id):
