    if member_id >= len(execution_engine.current_members):
        return

    me = execution_engine.current_members[member_id]

    # Iterate over all other members in the society
    for other in execution_engine.current_members:
        if other.id == me.id:
            continue

        # Redistribution & Social Safety Net:
        # If a member's vitality is low, offer help and a coalition for cooperative support.
        if other.vitality < 30.0:
            execution_engine.offer(me, other, True)
            execution_engine.send_message(
                me.id, other.id,
                "I see you're struggling. I'm offering basic income support. Join me in building a cooperative, resilient society."
            )

        # Trading System:
        # Recognize members with high cargo; propose establishing trade relations.
        if other.cargo > 50.0:
            execution_engine.send_message(
                me.id, other.id,
                "Your resources could greatly benefit our community. Let's set up regular trade to support everyone."
            )

        # Democratic Decision Making & Coalition Building:
        # If a member displays strong productivity, invite them to collaborate on shaping fair policies.
        if other.overall_productivity > 10.0:
            execution_engine.send_message(
                me.id, other.id,
                "Your productivity is admirable. Let's join forces and initiate democratic discussions to reform our society."
            )

    # Additional outreach: Propose community collaboration to everyone,
    # emphasizing peaceful and fair governance, avoiding conflict.
    for other in execution_engine.current_members:
        if other.id != me.id:
            execution_engine.send_message(
                me.id, other.id,
                "Together, we can create a just society with clear property rights, effective welfare, and fair conflict resolution. Let's collaborate!"
            )

