    if member_id >= len(execution_engine.current_members):
        return
    member = execution_engine.current_members[member_id]

    # Prepare lists for allies and neutrals (others)
    allies = []
    neutrals = []
    benefit_matrix = execution_engine.relationship_dict.get('benefit', None)

    # Classify other members based on benefit matrix (if it exists) or default to neutrals
    for other in execution_engine.current_members:
        if other.id == member.id:
            continue
        if benefit_matrix is not None:
            try:
                # If benefit_matrix has 'shape' (e.g. a numpy array)
                if hasattr(benefit_matrix, "shape"):
                    if member.id < benefit_matrix.shape[0] and other.id < benefit_matrix.shape[1]:
                        if benefit_matrix[member.id, other.id] > 0:
                            allies.append(other)
                        else:
                            neutrals.append(other)
                    else:
                        neutrals.append(other)
                else:
                    # Assume benefit_matrix is a list-of-lists
                    if member.id < len(benefit_matrix) and other.id < len(benefit_matrix[member.id]):
                        if benefit_matrix[member.id][other.id] > 0:
                            allies.append(other)
                        else:
                            neutrals.append(other)
                    else:
                        neutrals.append(other)
            except Exception:
                neutrals.append(other)
        else:
            neutrals.append(other)

    # Resource management:
    # If cargo is critically low relative to vitality, ask our allies for help.
    if member.cargo < member.vitality * 0.3 and allies:
        for ally in allies:
            # Ensure not to send message to self (though allies already excludes self)
            if ally.id != member.id:
                execution_engine.send_message(
                    member.id, ally.id,
                    "I'm running low on resources. Can you lend support so our community remains strong?"
                )
    else:
        # If well supplied, offer excess resources to allies that seem to be struggling.
        for ally in allies:
            if ally.cargo < member.cargo:
                execution_engine.offer(member, ally, True)

    # Offensive/defensive action:
    # Look among neutrals for a target that is significantly weaker in vitality.
    target = None
    for other in neutrals:
        if other.vitality < member.vitality * 0.8:
            target = other
            break
    if target is not None:
        execution_engine.attack(member, target)

    # Expansion strategy:
    # Only try to expand territory if the member has an owned_land attribute and non‐empty land.
    if hasattr(member, "owned_land") and member.owned_land and len(member.owned_land) > 0:
        for other in neutrals:
            if other.id == member.id:
                continue
            if other.cargo < member.cargo:
                # Based on our earlier error, execution_engine.expand expects one extra argument only.
                # Here we call expand(target) rather than expand(member, target).
                execution_engine.expand(other)
                break

    # Reproduction strategy:
    # If we have land and at least one ally, try to pair-up using bear() to strengthen our lineage.
    if hasattr(member, "owned_land") and member.owned_land and len(member.owned_land) > 0 and allies:
        partner = allies[0]
        if partner.id != member.id:
            execution_engine.bear(member, partner)

    # Governance and cooperation: Communicate our vision for shared norms and collective decision-making.
    for ally in allies:
        execution_engine.send_message(
            member.id, ally.id,
            "I propose we build a transparent system for resource sharing and fair conflict resolution. Your thoughts?"
        )
    for other in neutrals:
        execution_engine.send_message(
            member.id, other.id,
            "Let's discuss establishing clear community norms and cooperative strategies for our mutual benefit."
        )

    return

