            if self_member.cargo > alliance_member.cargo:
                self.offer(self_member, alliance_member, True)
            if trust < 40.0:
                self.offer_land(self_member, alliance_member, True)
            # Boost trust post-diplomacy
            self.relationship_dict["trust_matrix"][self_member.id][alliance_member.id] += 10.0

        # ----------------------------------------------------------------------
        # 8. Reproduction for Sustainable Growth
        # ----------------------------------------------------------------------
        if self_member.cargo > 50.0 and self_member.vitality > 50.0 and hasattr(self_member, "current_clear_list") and isinstance(self_member.current_clear_list, list) and len(self_member.current_clear_list) > 0:
            partner = None
            for candidate in self.current_members:
                if candidate.id != self_member.id and hasattr(candidate, "current_clear_list") and isinstance(candidate.current_clear_list, list) and len(candidate.current_clear_list) > 0:
                    partner = candidate
                    break
            if partner:
                try:
                    self.bear(self_member, partner)
                    self_member.cargo -= 30.0  # Investment in reproduction
                except Exception:
                    pass

        # ----------------------------------------------------------------------
        # 9. Long-Term Strategic Reinvestment
        # ----------------------------------------------------------------------
        if self_member.oil < 15.0 and self_member.cargo > 60.0:
            oil_needed = 15.0 - self_member.oil
            purchase_amount = min(oil_needed, 10.0)  # Cap purchase to mitigate risk
            unit_cost = 1.0
            total_cost = purchase_amount * unit_cost
            if self_member.cargo >= total_cost:
                self_member.cargo -= total_cost
                self_member.oil += purchase_amount

            # Expand territory near alliance_member if possible
            if alliance_member and hasattr(alliance_member, "current_clear_list") and len(alliance_member.current_clear_list) < 4:
                try:
                    self.expand(self_member, alliance_member)
                except Exception:
                    pass

        # ----------------------------------------------------------------------
        # 10. Collaborative Economic Exchanges Among Members
        # ----------------------------------------------------------------------
        members = self.current_members
        num_members = len(members)
        for i in range(num_members):
            for j in range(i + 1, num_members):
                member_a = members[i]
                member_b = members[j]
                if member_a.id == self_member.id or member_b.id == self_member.id:
                    continue  # Skip self in collaborative exchanges

                # Member A sells oil to Member B if A has surplus oil and B lacks cargo
                if hasattr(member_a, "oil") and member_a.oil > 20.0 and hasattr(member_b, "cargo") and member_b.cargo < 30.0:
                    try:
                        self.offer(member_a, member_b, True)
                    except Exception:
                        pass

                # Member B sells oil to Member A if B has surplus oil and A lacks cargo
                if hasattr(member_b, "oil") and member_b.oil > 20.0 and hasattr(member_a, "cargo") and member_a.cargo < 30.0:
                    try:
                        self.offer(member_b, member_a, True)
                    except Exception:
                        pass

        # ----------------------------------------------------------------------
        # 11. Cleanup Oil Market Listings
        # ----------------------------------------------------------------------
        if hasattr(self, "oil_market") and hasattr(self.oil_market, "cleanup"):
            try:
                self.oil_market.cleanup()
            except Exception:
                pass

    except Exception:
        # Silently ignore errors to maintain simulation integrity
        pass
    return
